<?php/** * The template for displaying all pages * * This is the template that displays all pages by default. * Please note that this is the WordPress construct of pages * and that other 'pages' on your WordPress site may use a * different template. * * @link https://developer.wordpress.org/themes/basics/template-hierarchy/ * * @package Foodcoop */    $getoptions = get_option('foodcoop_plugin_options');    $colorbg2 = esc_attr($getoptions['colorbg2']);get_header();?>    <script type="text/javascript">        jQuery(document).ready(function() {               jQuery("#masthead").css("min-height","0");               jQuery("#masthead").css("height","50px");               jQuery("#masthead").css("background-image","none");               jQuery("#masthead").css("background-color","<?php echo $colorbg2; ?>");               jQuery(".header-content").hide();        });    </script>	<main id="primary" class="site-main">		<?php		while ( have_posts() ) :			the_post();			get_template_part( 'template-parts/content', 'page' );			// If comments are open or we have at least one comment, load up the comment template.			if ( comments_open() || get_comments_number() ) :				comments_template();			endif;		endwhile; // End of the loop.		?>	</main><!-- #main --><?php           get_footer();